import React, { useState, useEffect, useRef } from 'react';
import MessageList from './MessageList';
import MessageInput from './MessageInput';
import ResetButton from './ResetButton';
import TypingIndicator from './TypingIndicator';
import apiService from '../services/api';
import './ChatScreen.scss';

const ChatScreen = () => {
  const [messages, setMessages] = useState([]);
  const [sessionId, setSessionId] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef(null);

  // Generate or retrieve session ID
  useEffect(() => {
    const storedSessionId = localStorage.getItem('rag-news-session-id');
    if (storedSessionId) {
      setSessionId(storedSessionId);
      loadSessionHistory(storedSessionId);
    } else {
      const newSessionId = generateSessionId();
      setSessionId(newSessionId);
      localStorage.setItem('rag-news-session-id', newSessionId);
    }
  }, []);

  // Auto-scroll to bottom when new messages arrive
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const generateSessionId = () => {
    return 'session_' + Math.random().toString(36).substr(2, 9) + '_' + Date.now();
  };

  const loadSessionHistory = async (sessionId) => {
    try {
      const history = await apiService.getSessionHistory(sessionId);
      if (history.messages && history.messages.length > 0) {
        setMessages(history.messages);
      }
    } catch (error) {
      console.error('Failed to load session history:', error);
    }
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleSendMessage = async (messageText) => {
    if (!messageText.trim() || isLoading) return;

    const userMessage = {
      id: Date.now(),
      text: messageText,
      role: 'user',
      timestamp: new Date().toISOString()
    };

    setMessages(prev => [...prev, userMessage]);
    setIsLoading(true);
    setIsTyping(true);

    try {
      const response = await apiService.sendMessage(sessionId, messageText);

      // Simulate typing effect for better UX
      setTimeout(() => {
        const botMessage = {
          id: Date.now() + 1,
          text: response.reply,
          role: 'assistant',
          timestamp: new Date().toISOString(),
          citations: response.citations || []
        };

        setMessages(prev => [...prev, botMessage]);
        setIsTyping(false);
        setIsLoading(false);
      }, 1000 + Math.random() * 2000); // Random delay between 1-3 seconds

    } catch (error) {
      console.error('Error sending message:', error);
      const errorMessage = {
        id: Date.now() + 1,
        text: 'Sorry, I encountered an error. Please try again.',
        role: 'assistant',
        timestamp: new Date().toISOString(),
        isError: true
      };

      setMessages(prev => [...prev, errorMessage]);
      setIsTyping(false);
      setIsLoading(false);
    }
  };

  const handleResetSession = async () => {
    if (!sessionId) return;

    try {
      await apiService.resetSession(sessionId);
      setMessages([]);

      // Generate new session ID
      const newSessionId = generateSessionId();
      setSessionId(newSessionId);
      localStorage.setItem('rag-news-session-id', newSessionId);
    } catch (error) {
      console.error('Error resetting session:', error);
    }
  };

  return (
    <div className="chat-screen">
      <div className="chat-header">
        <h1>News Bot</h1>
        <p>Ask me anything about the latest news!</p>
        <ResetButton onReset={handleResetSession} />
      </div>

      <div className="chat-messages">
        <MessageList messages={messages} />
        {isTyping && <TypingIndicator />}
        <div ref={messagesEndRef} />
      </div>

      <div className="chat-input">
        <MessageInput 
          onSendMessage={handleSendMessage} 
          disabled={isLoading}
        />
      </div>
    </div>
  );
};

export default ChatScreen;
