import React from 'react';
import './Message.scss';

const Message = ({ message }) => {
  const isUser = message.role === 'user';
  const isError = message.isError;

  const formatTime = (timestamp) => {
    return new Date(timestamp).toLocaleTimeString([], { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  const scrollToCitation = (citationNumber) => {
    const citationElement = document.getElementById(`citation-${citationNumber}`);
    if (citationElement) {
      citationElement.scrollIntoView({ behavior: 'smooth', block: 'center' });

      citationElement.classList.add('citation-highlight');
      setTimeout(() => {
        citationElement.classList.remove('citation-highlight');
      }, 2000);
    }
  };

  const renderMessageText = (text) => {
    if (!text || isUser) {
      return text;
    }

    // Regular expression to find citation numbers like [1], [2], etc.
    const citationRegex = /\[(\d+)\]/g;
    const parts = text.split(citationRegex);

    return parts.map((part, index) => {
      // Check if this part is a citation number
      if (index % 2 === 1) {
        const citationNumber = parseInt(part);
        return (
          <span
            key={index}
            className="citation-number"
            onClick={() => scrollToCitation(citationNumber)}
            title={`Click to view source ${citationNumber}`}
          >
            [{part}]
          </span>
        );
      }
      return part;
    });
  };

  return (
    <div className={`message ${isUser ? 'user' : 'bot'} ${isError ? 'error' : ''}`}>
      <div className="message-content">
        <div className="message-text">
          {renderMessageText(message.text)}
        </div>

        {message.citations && message.citations.length > 0 && (
          <div className="citations">
            <div className="citations-header">Sources:</div>
            {message.citations.map((citation, index) => (
              <a
                key={index}
                id={`citation-${index + 1}`}
                href={citation.url}
                target="_blank"
                rel="noopener noreferrer"
                className="citation-link"
              >
                <span className="citation-index">[{index + 1}]</span>
                {citation.title}
                {citation.score && (
                  <span className="citation-score">
                    ({Math.round(citation.score * 100)}% match)
                  </span>
                )}
              </a>
            ))}
          </div>
        )}

        <div className="message-time">
          {formatTime(message.timestamp)}
        </div>
      </div>
    </div>
  );
};

export default Message;
